def generateCandidate(input, groundTruth) { 
    def agentsList = configuration.agentInstances; 
    def bestCandidate = null; 
    def bestCot = ''; 
    for (int chainLength = 2; chainLength <= configuration.maxAgentChainLength; chainLength++) { 
        def sequences = generateAgentSequences(agentsList, chainLength); 
        sequences.each { seq -> 
            def candidate = input; 
            def cot = ''; 
            seq.each { agent -> 
                def result = agent.generateCandidate(candidate); 
                candidate = result.candidate; 
                cot += result.metadata.chain_of_thought + ' -> '; 
            }; 
            if (evaluateCandidate(candidate, groundTruth)) { 
                bestCandidate = candidate; 
                bestCot = cot; 
                return [candidate: bestCandidate, metadata: [method: seq.collect{it.name}.join(' + '), chain_of_thought: bestCot, confidence: 0.95]]; 
            } 
        } 
    } 
    return [candidate: null, metadata: [method: 'DynamicCompositeRiskAgent', chain_of_thought: 'No valid composite risk assessment found.', confidence: 0.0]]; 
}

