scripts.fetchDataScript:
def fetchData(url) { 
    def response = httpGet(url); 
    if(response.status == 200) { return parseJson(response.body); } else { println 'Failed to fetch educational data from ' + url; return null; } 
}

scripts.trainingScript:
def processTraining(examples, context) { 
    int correct = 0; 
    examples.each { example -> 
        def input = example.input; def expected = example.expectedOutput; 
        def agentsList = configuration.agentInstances; 
        agentsList.each { agent -> 
            def result = agent.generateCandidate(input); 
            if(result.candidate == expected) { println 'Learning training success with agent ' + result.metadata.method; correct++; return; } 
        } 
    }; 
    def score = (correct * 100.0) / examples.size(); println 'Learning training score: ' + score; return score; 
}

scripts.evaluationScript:
def processEvaluation(examples, context) { 
    int correct = 0; 
    examples.each { example -> 
        def input = example.input; def expected = example.expectedOutput; 
        def candidate = processTraining([example], context); 
        if(candidate != null && candidate == expected) { correct++; } else { 
            def composite = DynamicCompositeLearningAgent.generateCandidate(input, expected); 
            if(composite.candidate == expected) { correct++; } 
        } 
    }; 
    def score = (correct * 100.0) / examples.size(); println 'Learning evaluation score: ' + score; return score; 
}

scripts.finalExamScript:
def processFinal(puzzleSet, context) { 
    def results = [];
    puzzleSet.each { puzzle -> 
        def input = puzzle.input; def expected = puzzle.expectedOutput; 
        def cot = nlQuery('Generate detailed chain-of-thought for educational evaluation of: ' + input.toString() + ' expecting: ' + expected.toString()); 
        def candidateScore = processEvaluation([puzzle], context); 
        results.add([ puzzle: puzzle, final_score: candidateScore, final_output: candidateScore >= 100 ? expected : 'Incomplete', chain_of_thought: cot ]); 
    }; 
    println 'Final learning evaluation completed for ' + results.size() + ' items.'; return results; 
}

